operations.body:
query GetLocations {
  locations {
    description
    id
    name
    photo
    __typename
  }
  ... @defer {
    reallySlowOperation {
      id
      __typename
    }
    __typename
  }
}

query GetMember {
  member {
    dateOfBirth
    firstName
    lastName
    memberId
    __typename
  }
}

query GetReviews {
  latestReviews {
    comment
    id
    rating
    __typename
  }
}
